% %      
% %      
%       for en=1:N
% %          
%           C(:,i)= Y(:,i)+sigma*randn(m,1);        %  Almacenamiento de los datos sintéticos
%           Xa_C(:,en,i)=X_chol(:,en,i)+KC*(C(:,i)-H*X_chol(:,en,i));
% % 
%       end
% %      
%       X_chol(:,:,i+1)=Xa_C(:,:,i);
%     
%   meanxa_EnKF_CHOLESKY(:,i)=mean(X_chol(:,:,i+1),2);
% %      
% end                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     
%  if sum(muestreo~=i) 
%  
%   meanxa_EnKF_CHOLESKY(:,i)=mean(X_chol(:,:,i+1),2);
%  end
% 
% end
% % for i=1:Tsim
% % imagesc(BC(:,:,i))
% % colorbar
% % title(sprintf('Tiempo %i',i))
% % pause(2)
% % end

